package Ejercicios;

public class Bucles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 
		int w= 1;// VBLE W PARA EL BUCLE FOR
		System.out.println("Inicia el bucle for");
		System.out.println("");
		
		for( w=1;w<=7;w++){
	            System.out.println(w);
	        }
	        
		 int e=1;// VBLE e PARA EL BUCLE WHILE
		System.out.println("Inicia el bucle while");
		System.out.println("");
	       
	        while(e<=7){
	            System.out.println(e);
	            e++;
	        }
	}

}
