package Ejercicios;

public class Condicionales {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 
		int w= 1;// VBLE W PARA EL CONDICIONAL IF
			
		
		if (w>=18 && w<=20){
			System.out.println("Inicia el condicional IF");
			System.out.println("");
            System.out.println("Es número está dentro del rango");
        }
		 else {
			 	System.out.println("Inicia el condicional ELSE");
				System.out.println("");
				System.out.println("Ese número NO es el requerido");
        }
	}

}
